root.script:
(function() {
    GameEvents.Subscribe("petri_spawning_queue", function (args) {
        var queue = (Players.GetLocalPlayer() - args.queue);

        if (queue == 0) {
            $("#loadingLabel").text = $.Localize("spawning");
        } else {
            $("#loadingLabel").text = $.Localize("queued_for_spawn") + queue;
        }
    });
})();
